import React, { useState, useEffect } from 'react';
import './expense.css';
import Modal from 'react-modal';
import './modal.css';
import OrderTable from "./OrdersTable";

const ExpenseTracker = () => {
    const [transactions, setTransactions] = useState([]);
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState('');
    const [category, setCategory] = useState('');
    const [totalIncome, setTotalIncome] = useState(0);
    const [totalExpense, setTotalExpense] = useState(0);
    const [totalBalance, setTotalBalance] = useState(0);
    const [errorMessage, setErrorMessage] = useState('');
    const [showPopup, setShowPopup] = useState(false);
    const [currentPage, setCurrentPage] = useState('expense'); // 'expense' or 'income'

    const categories = {
        expense: ['Food', 'Transportation', 'Shopping', 'Bills'],
        income: ['Business', 'Salary', 'Incoming Transfer', 'Investment Returns', 'Other']
    };

    const addTransaction = (e) => {
        e.preventDefault();

        if (category === '' || amount.trim() === '') {
            setErrorMessage('Please select a category and enter the amount.');
            return;
        }

        const newTransaction = {
            description: category,
            amount: parseFloat(amount)
        };

        setTransactions([...transactions, newTransaction]);
        setDescription('');
        setAmount('');
        setCategory('');
        setErrorMessage('');
        setShowPopup(false);
    };

    useEffect(() => {
        calculateTotals();
    }, [transactions]);

    const calculateTotals = () => {
        let income = 0;
        let expense = 0;

        for (let i = 0; i < transactions.length; i++) {
            if (transactions[i].amount > 0) {
                income += transactions[i].amount;
            } else {
                expense += transactions[i].amount;
            }
        }

        setTotalIncome(income);
        setTotalExpense(expense);
        setTotalBalance(income + expense);
    };

    const renderCategories = () => {
        if (currentPage === 'expense') {
            return (
                <div className="category-group">
                    <h4>Expense</h4>
                    {categories.expense.map((category, index) => (
                        <button
                            key={index}
                            className={`category-item ${category === category ? 'active' : ''}`}
                            onClick={() => {
                                setCategory(category);
                                setShowPopup(false);
                            }}
                        >
                            {category}
                        </button>
                    ))}
                </div>
            );
        } else if (currentPage === 'income') {
            return (
                <div className="category-group">
                    <h4>Income</h4>
                    {categories.income.map((category, index) => (
                        <button
                            key={index}
                            className={`category-item ${category === category ? 'active' : ''}`}
                            onClick={() => {
                                setCategory(category);
                                setShowPopup(false);
                            }}
                        >
                            {category}
                        </button>
                    ))}
                </div>
            );
        }
    };


    return (
        <div className="container">
            <form onSubmit={addTransaction} className="form">
                <div className="form-group">
                    <div className="balance">
                        <h3>Your Balance:</h3>
                        <span>{totalBalance < 0 ? '-' : ''}₹{Math.abs(totalBalance)}</span>
                    </div>
                    <button
                        type="button"
                        className="category-btn"
                        onClick={() => setShowPopup(true)}
                    >
                        {category ? category : 'Select Category'}
                    </button>
                </div>
                <Modal
                    isOpen={showPopup}
                    onRequestClose={() => setShowPopup(false)}
                    contentLabel="Select a Category"
                    className="popup"
                    overlayClassName="overlay"
                >
                    <div className="category-nav">
                        <button
                            className={`category-nav-item ${currentPage === 'expense' ? 'active' : ''}`}
                            onClick={() => setCurrentPage('expense')}
                        >
                            Expense
                        </button>
                        <button
                            className={`category-nav-item ${currentPage === 'income' ? 'active' : ''}`}
                            onClick={() => setCurrentPage('income')}
                        >
                            Income
                        </button>
                    </div>
                    <div className="category-list">{renderCategories()}</div>
                </Modal>
                <div className="amount-input">
                    <label htmlFor="amount">Amount</label>
                    <input
                        type="number"
                        id="amount"
                        placeholder="Enter amount"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                    />
                </div>
                {errorMessage && <p className="error">{errorMessage}</p>}
                <button type="submit" className="btn">Add Transaction</button>
            </form>
            <div className="summary">
                <div className="summary-item">
                    <h3>Income:</h3>
                    <span className={`summary-amount ${totalIncome > 0 ? 'green' : 'black'}`}>
            ₹{totalIncome}
          </span>
                </div>
                <div className="summary-item">
                    <h3>Expense:</h3>
                    <span className={`summary-amount ${totalExpense < 0 ? 'red' : 'black'}`}>
            {totalExpense < 0 && '-'}₹{Math.abs(totalExpense)}
          </span>
                </div>
            </div>
            <div className="summary-item">
                <ul className="transaction-list">
                    <h3>History</h3>
                    {transactions.map((transaction, index) => (
                        <li key={index} className="transaction-item">
                            <div className="transaction-description">
                                {transaction.description}
                            </div>
                            <div className="transaction-amount">
                <span className={`amount-text ${transaction.amount > 0 ? 'income' : 'expense'}`}>
                  {transaction.amount >= 0 ? '₹' : '-₹'}
                    {Math.abs(transaction.amount)}
                </span>
                            </div>
                            {index !== transactions.length - 1 && <hr className="transaction-line" />}
                        </li>
                    ))}
                </ul>
            </div>
            <div><OrderTable/></div>
        </div>
    );
};

export default ExpenseTracker;
