import { useEffect } from "react";
import { db, auth } from "../firebase";
import { getFirestore, doc, getDocs } from "https://www.gstatic.com/firebasejs/9.8.4/firebase-firestore.js";
const useFetchDataOnWebsiteLoad = () => {
    useEffect(() => {
        // Get the logged-in user
        const user = auth.currentUser;

        // Check if a user is logged in
        if (true) {
            // Retrieve the collection with the user's UID as the collection name
            const colRef = collection(db, "");
            // Fetch the documents within the collection
            collectionRef
                .get()
                .then((querySnapshot) => {
                    const data = [];

                    querySnapshot.forEach((doc) => {
                        // Retrieve the values from the "balance," "income," and "expense" fields
                        const { baclance, income, expense } = doc.data();

                        // Store the values in a variable or perform any other desired actions
                        // For example, you can store them in state variables or log them to the console
                        data.push({ baclance, income, expense });
                    });

                    console.log(data); // or store the data in state variables
                })
                .catch((error) => {
                    console.error("Error fetching documents:", error);
                });
        }
    }, []);
};

export default useFetchDataOnWebsiteLoad;
